import { defineStore } from 'pinia';

export const useOrder = defineStore({
  id: 'order',
  state: () => ({ 
    customer:[],
    customerName: '',
    customerAddres: '',
    customerPhone: '',
    storeLocation: "Sari Mulya Pasarbatang",
    orderMethod: 'COD',
  }),
  actions: {
    invalid() {
        if (this.orderStore.customerName == '' && this.orderStore.customerAddres == '' && this.orderStore.customerPhone == '') {
            return true
        } else {
            return false
        }
    }
  },
});
