import { ref, onMounted } from 'vue';

//import api
import api from '../../api';

//define state

export default {
  __name: 'ProductList',
  setup(__props, {
    expose: __expose
  }) {
    __expose();

    //import ref and onMounted
    const products = ref([]);

    //method fetchDataproducts
    const fetchDataProducts = async () => {
      //fetch data 
      await api.get('/api/products').then(response => {
        //set response data to state "products"
        products.value = response.data.data.data;
      });
    };

    //run hook "onMounted"
    onMounted(() => {
      //call method "fetchDataproducts"
      fetchDataProducts();
    });
    const __returned__ = {
      products,
      fetchDataProducts,
      ref,
      onMounted,
      get api() {
        return api;
      }
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
