import { defineStore } from 'pinia';

export const useCart = defineStore({
  id: 'cart',
  state: () => ({ 
    cartItems: [], // Mengubah nama menjadi cartItems
  }),
  actions: {
    addToCart(product) {
      // Mengecek apakah produk sudah ada di keranjang
      const existingItem = this.cartItems.find(item => item.product.id === product.id);

      if (existingItem) {
        // Jika sudah ada, tambahkan qty
        existingItem.qty++;
      } else {
        // Jika belum ada, tambahkan produk baru
        this.cartItems.push({ product, qty: 1 });
      }

      console.log("Adding", product, " to ", this.cartItems);
    },
  },
});
