// src/store/index.js
import { defineStore } from 'pinia';

export const pinia = defineStore();

export const store = pinia.store({
  state: () => ({
    sharedData: null,
  }),
  actions: {
    setSharedData(data) {
      this.sharedData = data;
    },
  },
});
