import { defineStore } from 'pinia';

export const useCart = defineStore({
  id: 'cart',
  state: () => ({ 
    cartItem: null,
  }),
  actions: {
    addToCart(product){
      this.cartItem.push = product;
      console.log("Adding", product, " to ", this.cartItem)
    },
}
});
