import { defineStore } from 'pinia';

export const useOrder = defineStore({
  id: 'order',
  state: () => ({ 
    customer:[],
    customerName: '',
    customerAddres: '',
    customerPhone: '',
    storeLocation: "Sari Mulya Pasarbatang",
    orderMethod: 'COD',
    total: 0
  }),
  actions: {
  },
  getters: {
    invalid() {
      if (this.customerName == '' && this.customerAddres == '' && this.customerPhone == '') {
          return true
      } else {
          return false
      }
  },
  getTotal(subtotal){
    if (this.orderMethod == 'COD') {
      this.total = subtotal+3000
    } else {
      this.total = subtotal
    }
    return this.total
  }
  }
});
