import { defineStore } from 'pinia'

export const useCategory = defineStore({
  // id is required so that Pinia can connect the store to the devtools
  id: 'loggedInUser',
  state: ()=>({ categories:  "Sebelum Test"}),
  getters: {},
  actions:{
    actions: {
        // Tambahkan action untuk menambah kategori
        addCategory: (newCategory) => {
          // this.state.categories merujuk ke state kategori pada store ini
          this.state.categories = newCategory
        },
      },
  }
})
