import { defineStore } from 'pinia';
import api from '../api';

export const useProducts = defineStore({
  id: 'products',
  state: () => ({ 
    products: [],
    currentPage: 1,
    lastPage: 1,
    error: null
  }),
  actions: {
    async fetchDataProducts(page = 1, query = '') {
      try {
        const params = { page };
        if (query) {
          params.query = query;
        }
        const response = await api.get('/api/products', { params });
        this.products = response.data.data.data;
        this.currentPage.value = response.data.data.current_page;
        this.lastPage.value = response.data.data.last_page;
        this.error = null;
      } catch (err) {
        this.error = 'Error fetching category data.';
      }
    },
  },
});
