import { defineStore } from 'pinia';

export const useCategory = defineStore({
  id: 'categories',
  state: () => ({ 
    name: 'All Products'
  }),
  actions: {
    setSelectedCategory(categoryName){
      this.name = categoryName;
    },
  },
});
